import AppBar from '../components/AppBar2'
import Drawer from '../components/Drawer2';
import * as React from 'react';
import Box from '@mui/material/Box';
import Container from '@mui/material/Container';
import Grid from '@mui/material/Grid';
import Paper from '@mui/material/Paper';
import Toolbar from '@mui/material/Toolbar';
import { styled, createTheme, ThemeProvider } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import Title from '../components/Title';
import BarChart from '../components/BarChart';



//Das ist mein Base Template auf dem alle Pages außer eventuell der Landing page aufbauen werden


const Dashboard = () => {

    const [open, setOpen] = React.useState(false);
    const [page, setPage] = React.useState("Dashboard");
    const defaultTheme = createTheme();

    return (
        
         <ThemeProvider theme={defaultTheme}>
            <Box sx={{ display: 'flex' }}>
                <CssBaseline />
                <AppBar open ={open} setOpen = {setOpen} page = {page}/>
                <Drawer open ={open} setOpen = {setOpen} page = {page}/>
                <Box
                component="main"
                sx={{
                    backgroundColor: (theme) =>
                    theme.palette.mode === 'light'
                        ? theme.palette.grey[100]
                        : theme.palette.grey[900],
                    flexGrow: 1,
                    height: '100vh',
                    overflow: 'auto',
                }}
                >
                    <Toolbar />
                    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
                        <Grid container spacing={3}>
                            <Grid item xs={7} md={7} lg={7}>
                                <Paper
                                        
                                    sx={{
                                        p: 2,
                                        display: 'flex',
                                        flexDirection: 'column',
                                        height: 'auto',
                                        alignItems: 'center',
                                        textAlign: "center",
                                        verticalAlign: "middle",
                                        justifyContent: "center",
                                    }}
                                    >
                                        <Title>Anzahl Wafer nach Stunden</Title>
                                        <BarChart/>
                                </Paper>
                            </Grid>
                            <Grid item xs={4} md={4} lg={4}>
                                <Paper
                                        
                                    sx={{
                                        p: 2,
                                        display: 'flex',
                                        flexDirection: 'column',
                                        height: 'auto',
                                        alignItems: 'center',
                                        textAlign: "center",
                                        verticalAlign: "middle",
                                        justifyContent: "center",
                                    }}
                                    >
                                        <Title>Ergebnisse der Farbanalyse in der letzten Stunde</Title>

                                </Paper>
                            </Grid>
                            <Grid item xs={10} md={10} lg={10}>
                                <Paper
                                        
                                    sx={{
                                        p: 2,
                                        display: 'flex',
                                        flexDirection: 'column',
                                        height: 'auto',
                                        alignItems: 'center',
                                        textAlign: "center",
                                        verticalAlign: "middle",
                                        justifyContent: "center",
                                    }}
                                    >
                                        <Title>Wafer Durchsatz in den letzetn 30 Sekunden</Title>

                                </Paper>
                            </Grid>
                                
                            
                        </Grid>
                    </Container>

                </Box>
            </Box>
        </ThemeProvider>
    );
}
    
export default Dashboard;
